
$(function(){var owl_main=$("#main-slide");var owl_blog=$("#blog-slide");var owl_testi=$("#testi-slide");var owl_best=$("#best-sell-slide");var owl_featured=$("#featured-slide");var owl_brand=$("#brand-slide");var time=7;var $progressBar,$bar,$elem,isPause,tick,percentTime;owl_main.owlCarousel({slideSpeed:800,pagination:false,singleItem:true,lazyLoad:true,transitionStyle:"fadeUp",afterInit:progressBar,afterMove:moved,startDragging:pauseOnDragging});function progressBar(elem){$elem=elem;buildProgressBar();start();}
function buildProgressBar(){$progressBar=$("<div>",{id:"progress-bar"});$bar=$("<div>",{id:"bar"});$progressBar.append($bar).prependTo($elem);}
function start(){percentTime=0;isPause=false;tick=setInterval(interval,10);}
function interval(){if(isPause===false){percentTime+=1/time;$bar.css({width:percentTime+"%"});if(percentTime>=100){$elem.trigger('owl.next');}}}
function pauseOnDragging(){isPause=true;}
function moved(){clearTimeout(tick);start();}
owl_blog.owlCarousel({slideSpeed:800,pagination:false,lazyLoad:true,singleItem:true});$("#blog-nav-right").click(function(){owl_blog.trigger('owl.next');});$("#blog-nav-left").click(function(){owl_blog.trigger('owl.prev');});owl_testi.owlCarousel({slideSpeed:800,pagination:false,lazyLoad:true,singleItem:true});$("#testi-nav-right").click(function(){owl_testi.trigger('owl.next');});$("#testi-nav-left").click(function(){owl_testi.trigger('owl.prev');});owl_best.owlCarousel({singleItem:true,lazyLoad:true,slideSpeed:800,pagination:false,itemsScaleUp:true});$("#best-nav-right").click(function(){owl_best.trigger('owl.next');});$("#best-nav-left").click(function(){owl_best.trigger('owl.prev');});owl_featured.owlCarousel({items:5,itemsDesktop:[1000,5],itemsDesktopSmall:[900,4],itemsTablet:[600,3],itemsMobile:[479,2],lazyLoad:true,slideSpeed:800,pagination:false,itemsScaleUp:true});$("#featured-nav-right").click(function(){owl_featured.trigger('owl.next');});$("#featured-nav-left").click(function(){owl_featured.trigger('owl.prev');});owl_brand.owlCarousel({items:5,itemsDesktop:[1000,5],itemsDesktopSmall:[900,4],itemsTablet:[600,3],itemsMobile:[479,2],lazyLoad:true,slideSpeed:800,pagination:false,itemsScaleUp:true});$("#brand-nav-right").click(function(){owl_brand.trigger('owl.next');});$("#brand-nav-left").click(function(){owl_brand.trigger('owl.prev');});});
